import React,{useEffect,useState} from 'react';
import { useTheme } from '@mui/material/styles';
import OutlinedInput from '@mui/material/OutlinedInput';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import axios from 'axios';

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};

function getStyles(name, personName, theme) {
  return {
    fontWeight:
      personName.indexOf(name) === -1
        ? theme.typography.fontWeightRegular
        : theme.typography.fontWeightMedium,
  };
}

export default function MultipleSelectPlaceholder(props) {
  const theme = useTheme();
  const [personName, setPersonName] = React.useState([]);
// console.log(props.names)
const names=props.names
  const handleChange = (event) => {
    const {
      target: { value },
    } = event;
    setPersonName(
      // On autofill we get a stringified value.
      typeof value === 'string' ? value.split(',') : value,
    );
  };

  const [data, setData] = useState([]);

console.log(personName)
  useEffect(()=>{
    
    const fetchData=()=>{
        try {
            const response2= axios.get("https://dummyjson.com/products?skip=1&limit=100").then(response => {
                // Access the data from the response
                const data = response.data;
          
                // Now you can work with the data
                console.log(data);
          
                // Return the data if needed
                   setData([...data.products])
                return data.products;
              })
            //   setData2([...response2])
            // console.log(data2)
      } catch (error) {
          console.log("error in fetching data")
      }
    }
   fetchData();


  },[])
    


  function filterProductsByCategory(products, selectedCategory) {
    // Use Array.filter to filter products based on the selected category
    const filteredProducts = products.filter(product => product.category === selectedCategory);
    // console.log(products)


    // console.log(selectedCategory)
    
    // console.log(filteredProducts)
    if(selectedCategory===""){
      return products
    }else {
      return filteredProducts;
    }
    
  }
  
  // Example usage:
  const filteredProducts = filterProductsByCategory(data, personName[0]);
  console.log(filteredProducts);
  console.log(personName[0])


  return (
    <div>
      <FormControl sx={{ m: 1, width: 350, mt: 3 }}>
        <Select
          single
          displayEmpty
          value={personName}
          onChange={handleChange}
          input={<OutlinedInput />}
          renderValue={(selected) => {
            if (selected.length === 0) {
              return <em></em>;
            }

            return selected.join(', ');
          }}
          MenuProps={MenuProps}
          inputProps={{ 'aria-label': 'Without label' }}
        >
          <MenuItem disabled value="">
            <em>Placeholder</em>
          </MenuItem>
          {names.map((name) => (
            <MenuItem
              key={name}
              value={name}
              style={getStyles(name, personName, theme)}
            >
              {name}
            </MenuItem>
          ))}
        </Select>
      </FormControl>
    </div>
  );
}
