const initialState = [];

const addToCart = (state = initialState, action) => {
  switch (action.type) {
    case "addtocart":
      const newItem = action.payload;

      // Check if an item with the same ID already exists in the state
      const isItemExist = state.some(item => item.id === newItem.id);

      // If the item doesn't exist, add it to the state
      if (!isItemExist) {
        return [...state, newItem];
      }

      // If the item already exists, return the current state
      return state;

    default:
      return state;
  }
};

export default addToCart;
