import React, { useState } from 'react';
import './CratDisplay.css'; // Make sure to have a CSS file for styling

const ProductList = () => {
  // Sample product data
  const products = [
    { id: 1, name: 'Product 1', price: 20, description: 'Description 1', image: 'product1.jpg' },
    { id: 2, name: 'Product 2', price: 30, description: 'Description 2', image: 'product2.jpg' },
    // Add more products as needed
  ];

  const [cart, setCart] = useState([]);

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  const calculateTotal = () => {
    return cart.reduce((total, product) => total + product.price, 0);
  };

  return (
    <div className="container">
      <div className="left">
        <h2>Product List</h2>
        <ul>
          {products.map((product) => (
            <li key={product.id}>
              <img src={product.image} alt={product.name} />
              <div>
                <h3>{product.name}</h3>
                <p>{product.description}</p>
                <p>${product.price}</p>
                <button onClick={() => addToCart(product)}>Add to Cart</button>
              </div>
            </li>
          ))}
        </ul>
      </div>
      <div className="right">
        <h2>Receipt</h2>
        <ul>
          {cart.map((product) => (
            <li key={product.id}>
              {product.name} - ${product.price}
            </li>
          ))}
        </ul>
        <p>Total: ${calculateTotal()}</p>
        <button>Checkout</button>
      </div>
    </div>
  );
};

export default ProductList;
