import React, { useState } from 'react';
import './CratDisplay.css'; // Import your CSS file
import { useSelector } from 'react-redux';
const ProductList = () => {
  const [selectedItems, setSelectedItems] = useState([]);

  const handleCheckout = (item) => {
    setSelectedItems((prevItems) => [...prevItems, item]);
  };
    const products=useSelector((state)=>state.productArray);
    console.log(products)
  return (
    <div className="product-container">
      <div className="product-list">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            <img src={product.thumbnail} alt={product.name} className="product-image" />
            <div className="product-details">
              <h3 className="product-name">{product.name}</h3>
              <p className="product-description">{product.description}</p>
              <p className="product-price">${product.price}</p>
              <button onClick={() => handleCheckout(product)} className="checkout-button">
                Checkout
              </button>
            </div>
          </div>
        ))}
      </div>

      <div className="receipt-container">
        <h2>Receipt</h2>
        <ul className="receipt-list">
          {selectedItems.map((item) => (
            <li key={item.id} className="receipt-item">
              {item.name} - ${item.price}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ProductList;
